import timefrom pages.login_page import Login_pageurl = 'https://stellarburgers.nomoreparties.site/'def test_transition_to_user_account(driver, login, password):    user_account_page = Login_page(driver, url)    driver.get(url)    user_account_page.click_user_acc_btn()    user_account_page.fill_inputs_login(login, password)    user_account_page.click_login_btn()    user_account_page.click_user_acc_btn()    time.sleep(1)    user_account_page.assert_url(f'{url}account/profile')def test_transition_from_user_account_click_to_constructor(driver):    user_account_page = Login_page(driver, url)    user_account_page.click_designer()    user_account_page.assert_url(url)def test_transition_from_user_account_click_to_logo(driver):    user_account_page = Login_page(driver, url)    user_account_page.click_user_acc_btn()    user_account_page.click_logo()    user_account_page.assert_url(url)